mapper.insert:
INSERT INTO reservation (member_id, room_id, check_in, check_out, total_amount)
SELECT #{memberId}, #{roomId}, #{checkIn}, #{checkOut}, #{totalAmount}
WHERE NOT EXISTS (
    SELECT 1 
    FROM reservation 
    WHERE room_id = #{roomId} 
    AND NOT (#{checkOut} <= check_in OR #{checkIn} >= check_out)
);
